import './../App.css';
import React from 'react';

export default class FindedPeople extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      curp : "",
      name : "",
      surname : "",
      birthDate : "",
      foundDate : "",
      addressFounded : "",
      hospitalized : false,
      condition : "",
      moreInfo : ""
    }
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  
  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(event) {
    createLostPerson(this.state.value);
    alert('Los datos han sido registrados correctamente');
    event.preventDefault();
  }

  render(){
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <h3> Ingresa los datos de la persona que se encontró</h3>
          <label>
            CURP
            <input type="text" name="curp" value={this.state.curp} onChange={this.handleInputChange}/>
          </label>
          <br />
          <label>
            Nombre(s)
            <input type="text" name="name" value={this.state.name} onChange={this.handleInputChange}/>
          </label>
          <br />
          <label>
            Apellido(s)
            <input type="text" name="surname" value={this.state.surname} onChange={this.handleInputChange}/>
          </label>
          <br />
          <label>
            Fecha de Nacimiento
            <input type="date" name="birhDate" value={this.state.birthDate} onChange={this.handleInputChange}/>
          </label>
          <br />
          <label>
            Día que se encontró
            <input type="date" name="foundDate" value={this.state.foundDate} onChange={this.handleInputChange}/>
          </label>
          <br />
          <label>
            Lugar donde se encontró
            <input type="text" name="addressFounded" value={this.state.addressFounded} onChange={this.handleInputChange}/>
          </label>
          <br />
          <fieldset>
          <label>
            ¿Está hospitalizado?
              <label><input type="radio" id="si" name="hospitalized" value={this.state.hospitalized} onChange={this.handleInputChange}/>Sí</label>
              <label><input type="radio" id="no" name="hospitalized" value={this.state.hospitalized} onChange={this.handleInputChange}/>No</label>
          </label>
          </fieldset>
          <label>
            Condition
            <input type="text" name="condition" value={this.state.condition} onChange={this.handleInputChange}/>
          </label>
          <br />
          <label>
            Más información
            <input type="text" name="moreInfo" value={this.state.moreInfo} onChange={this.handleInputChange}/>
          </label>
          <br />
          <input type="submit" value="Enviar" />
        </form>
      </div >
    );
  }
}
